import{_ as a,c as t,a2 as e,o as i}from"./chunks/framework.C9NVOr0y.js";const u=JSON.parse('{"title":"Установка WSL","description":"","frontmatter":{"title":"Установка WSL"},"headers":[],"relativePath":"manuals/01-how-to-setup-work-environment/02-01-how-to-install-wsl.md","filePath":"manuals/01-how-to-setup-work-environment/02-01-how-to-install-wsl.md","lastUpdated":1732039776000}'),l={name:"manuals/01-how-to-setup-work-environment/02-01-how-to-install-wsl.md"};function n(o,s,p,d,h,c){return i(),t("div",null,s[0]||(s[0]=[e(`<h1 id="как-установить-windows-subsystem-for-linux" tabindex="-1">Как установить Windows Subsystem for Linux? <a class="header-anchor" href="#как-установить-windows-subsystem-for-linux" aria-label="Permalink to &quot;Как установить Windows Subsystem for Linux?&quot;">​</a></h1><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p>Установка WSL требуется только при работе на Windows.</p></div><div class="important custom-block github-alert"><p class="custom-block-title">IMPORTANT</p><p>Многие CLI инструменты, которые мы используем и разрабатываем, требуют UNIX-совместимого окружения, поэтому <strong>установка WSL является обязательной при работе на Windows</strong>.</p></div><h2 id="запустите-установку" tabindex="-1">Запустите установку <a class="header-anchor" href="#запустите-установку" aria-label="Permalink to &quot;Запустите установку&quot;">​</a></h2><p>Откройте терминал <code>cmd.exe</code> <strong>от имени администратора</strong> и выполните команду:</p><div class="language-bat vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bat</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">wsl</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> --install</span></span></code></pre></div><p>Эта команда автоматически установит WSL 2 и дистрибутив Ubuntu.</p><h2 id="перезагрузите-windows" tabindex="-1">Перезагрузите Windows <a class="header-anchor" href="#перезагрузите-windows" aria-label="Permalink to &quot;Перезагрузите Windows&quot;">​</a></h2><p>После выполнения команды <code>wsl --install</code>, перезагрузите Windows. После перезагрузки дождитесь завершения установки WSL в окне, которое откроется автоматически.</p><h2 id="проверьте-корректность-установки" tabindex="-1">Проверьте корректность установки <a class="header-anchor" href="#проверьте-корректность-установки" aria-label="Permalink to &quot;Проверьте корректность установки&quot;">​</a></h2><p>После завершения установки, убедитесь, что WSL установлен корректно, выполнив следующую команду в <code>cmd.exe</code>:</p><div class="language-bat vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bat</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">wsl</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> --list --verbose</span></span></code></pre></div><p>Эта команда выведет список установленных дистрибутивов и версию WSL для каждого дистрибутива.</p><div class="language-bat vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bat</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">  NAME                   STATE              VERSION</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">* Ubuntu                Running                </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">2</span></span></code></pre></div><p>Если в таблице отображается дистрибутив Ubuntu и WSL версии 2, установка прошла успешно.</p><h2 id="как-запустить-wsl" tabindex="-1">Как запустить WSL? <a class="header-anchor" href="#как-запустить-wsl" aria-label="Permalink to &quot;Как запустить WSL?&quot;">​</a></h2><p>Есть несколько вариантов:</p><ol><li>Открыть терминал с названием <code>Ubuntu</code>.</li><li>Открыть терминал <code>cmd.exe</code> и выполнить команду:<div class="language-bat vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bat</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">wsl</span></span></code></pre></div></li><li>Открыть терминал <code>PowerShell</code> и выполнить команду:<div class="language-PowerShell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">PowerShell</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">wsl</span></span></code></pre></div></li></ol>`,18)]))}const b=a(l,[["render",n]]);export{u as __pageData,b as default};
