import{_ as t,c as e,a2 as o,o as s}from"./chunks/framework.C9NVOr0y.js";const u=JSON.parse('{"title":"Поиск и устранение проблем","description":"","frontmatter":{"title":"Поиск и устранение проблем"},"headers":[],"relativePath":"manuals/04-how-to-access-to-vpn/04-troubleshooting.md","filePath":"manuals/04-how-to-access-to-vpn/04-troubleshooting.md","lastUpdated":1732116926000}'),l={name:"manuals/04-how-to-access-to-vpn/04-troubleshooting.md"};function r(i,a,c,n,p,m){return s(),e("div",null,a[0]||(a[0]=[o('<h1 id="поиск-и-устранение-проблем" tabindex="-1">Поиск и устранение проблем <a class="header-anchor" href="#поиск-и-устранение-проблем" aria-label="Permalink to &quot;Поиск и устранение проблем&quot;">​</a></h1><h2 id="часто-задаваемые-вопросы" tabindex="-1">Часто задаваемые вопросы <a class="header-anchor" href="#часто-задаваемые-вопросы" aria-label="Permalink to &quot;Часто задаваемые вопросы&quot;">​</a></h2><h3 id="macos" tabindex="-1">macOS <a class="header-anchor" href="#macos" aria-label="Permalink to &quot;macOS&quot;">​</a></h3><details><summary>FortiClient VPN разрывает соединение, когда я закрываю крышку макбука</summary><div class="note custom-block github-alert"><p class="custom-block-title">Что делать?</p><p>Ничего делать не требуется. Такое поведение настроено службой безопасности.</p></div></details><details><summary>FortiClient VPN разрывает соединение, когда macOS просит ввести пароль пользователя</summary><div class="note custom-block github-alert"><p class="custom-block-title">Что делать?</p><p>Ничего делать не требуется. Такое поведение настроено службой безопасности.</p></div></details><details><summary>FortiClient VPN разрывает соединение, когда я подключаюсь к другому VPN</summary><div class="note custom-block github-alert"><p class="custom-block-title">Что делать?</p><p>Ничего делать не требуется. Такое поведение настроено службой безопасности.</p></div></details><details><summary>FortiClient VPN регулярно разрывает соединение по неизвестной причине и инженеры поддержки не могут мне помочь</summary><div class="note custom-block github-alert"><p class="custom-block-title">Что делать?</p><p>Попробуйте установить на macOS приложение FortiClient VPN, предназначенное для iPadOS. Скачать приложение для iPadOS можно на <a href="https://apps.apple.com/kz/app/forticlient-vpn/id1475674905" target="_blank" rel="noreferrer">этой странице в App Store</a>.</p><div class="warning custom-block github-alert"><p class="custom-block-title">Обратите внимание</p><p>Если приложение недоступно в вашем регионе, необходимо будет сменить регион в App Store. Как это сделать можно прочитать в <a href="https://journal.tinkoff.ru/apple-region/" target="_blank" rel="noreferrer">этой статье</a>.</p></div></div></details><h2 id="обращение-в-поддержку" tabindex="-1">Обращение в поддержку <a class="header-anchor" href="#обращение-в-поддержку" aria-label="Permalink to &quot;Обращение в поддержку&quot;">​</a></h2><p>Если ваша проблема не описана ни в одном из кейсов выше, вы можете обратиться к инженерам поддержки за помощью.</p><p>Для этого отправьте сообщение с описанием вашей проблемы в Slack в чат <code>infra</code>.</p>',10)]))}const h=t(l,[["render",r]]);export{u as __pageData,h as default};
